************************************************************************      subroutine d2func(x,aa,f2a,ncef,kerr)************************************************************************      implicit double precision(a-h,o-z)      dimension aa(30),f2a(30,30)      kerr = 0      arg1 = aa(2) * x      arg2 = aa(4) * x      arg3 = aa(6) * x      x2 = x * x      f2a(1,1) = 0.d0      f2a(1,2) = x * dexp(arg1)      f2a(1,3) = 0.d0      f2a(1,4) = 0.d0      f2a(1,5) = 0.d0      f2a(1,6) = 0.d0      f2a(2,1) = f2a(1,2)      f2a(2,2) = aa(1) * x2 * exp(arg1)      f2a(2,3) = 0.d0      f2a(2,4) = 0.d0      f2a(2,5) = 0.d0      f2a(2,6) = 0.d0      f2a(3,1) = 0.d0      f2a(3,2) = 0.d0      f2a(3,3) = 0.d0      f2a(3,4) = x * dexp(arg2)      f2a(3,5) = 0.d0      f2a(3,6) = 0.d0      f2a(4,1) = 0.d0      f2a(4,2) = 0.d0      f2a(4,3) = f2a(3,4)      f2a(4,4) = aa(3) * x2 * exp(arg2)      f2a(4,5) = 0.d0      f2a(4,6) = 0.d0      f2a(5,1) = 0.d0      f2a(5,2) = 0.d0      f2a(5,3) = 0.d0      f2a(5,4) = 0.d0      f2a(5,5) = 0.d0      f2a(5,6) = x * dexp(arg3)      f2a(6,1) = 0.d0      f2a(6,2) = 0.d0      f2a(6,3) = 0.d0      f2a(6,4) = 0.d0      f2a(6,5) = f2a(5,6)      f2a(6,6) = aa(5) * x2 * exp(arg3)      return      end