************************************************************************      subroutine dfunc(x,aa,fa,ncef,kerr)************************************************************************      implicit double precision(a-h,o-z)      dimension aa(30),fa(30)      kerr = 0      arg1 = aa(2) * x      arg2 = aa(4) * x      arg3 = aa(6) * x      fa(1) = dexp(arg1)      fa(2) = aa(1) * x * dexp(arg1)      fa(3) = dexp(arg2)      fa(4) = aa(3) * x * dexp(arg2)      fa(5) = dexp(arg3)      fa(6) = aa(5) * x * dexp(arg3)      return      end